mapper.select:
SELECT p.post_id, p.user_id, p.title, p.content, p.is_notice, p.created_at, p.updated_at FROM community_post p WHERE p.is_deleted = 'N'
AND (p.title LIKE CONCAT('%', #{keyword}, '%') OR p.content LIKE CONCAT('%', #{keyword}, '%'))
AND p.user_id = #{userId}
ORDER BY p.created_at DESC LIMIT #{limit} OFFSET #{offset}

SELECT COUNT(*) FROM community_post p WHERE p.is_deleted = 'N'
AND (p.title LIKE CONCAT('%', #{keyword}, '%') OR p.content LIKE CONCAT('%', #{keyword}, '%'))
AND p.user_id = #{userId}
