mapper.select:
SELECT comment_id, post_id, user_id, content, created_at FROM community_comment WHERE is_deleted = 'N'
AND content LIKE CONCAT('%', #{keyword}, '%')
AND user_id = #{userId}
ORDER BY created_at DESC LIMIT #{limit} OFFSET #{offset}

SELECT COUNT(*) FROM community_comment WHERE is_deleted = 'N'
AND content LIKE CONCAT('%', #{keyword}, '%')
AND user_id = #{userId}
